INSERT INTO db_ricette.recipes(title, photoUrL, ingredients, portions, preparation_time, recipe_text)VALUES('Risotto allo Zafferano', 'https://www.giallozafferano.it/images/ricette/174/17481/foto_hd/hd650x433_wm.jpg', 'Zafferano in pistilli 1 cucchiaino, Riso Carnaroli 320g, Burro 125g, Cipolle 1, Grana Padano DOP da grattugiare 80g, Vino bianco 40g, Acqua q.b., Brodo vegetale 1l, Sale fino q.b.', 4, 25, '.....' );
INSERT INTO db_ricette.recipes(title, photoUrL, ingredients, portions, preparation_time, recipe_text)VALUES('Spaghetti Cacio e Pepe', 'https://www.giallozafferano.it/images/219-21989/Spaghetti-Cacio-e-Pepe_450x300.jpg', 'Spaghetti 320g, Pecorino romano stagionatura media, da grattugiare 200g, Pepe nero in grani 5g', 4, 15, 'Cuoci gli spaghetti al dente. Tosta il pepe, aggiungi acqua di cottura, unisci Pecorino e crea una crema. Manteca la pasta con la crema, servendo con Pecorino e pepe.' );